module.exports = function () {
    Array.prototype.remove = function (obj) {
        var index = this.indexOf(obj);
        if (index == -1) {
            return false;
        }
        this.splice(index, 1);
        return true;
    };

    Number.prototype.toByte = function (bit, length) {
        bit = bit ? bit : HeadLength;
        var result = new Buffer(bit);
        for (var i = 0; i < bit; i++) {
            result[i] = (this >> (i * 8)) & 0xff;
        }
        return result;
    };

    Buffer.prototype.toNum = function (bit) {
        bit = bit ? bit : HeadLength;
        var result = 0;
        for (var i = 0; i < bit; i++) {
            result += (this[i] << (i * 8));
        }
        return result;
    };
};
